<?php/** * Created by PhpStorm. * User: Baymax * Date: 2017/12/21 * Time: 15:00 */namespace app\Admin\controller;use think\Request;use app\Admin\model\Setting;use app\Common\Controller\AuthAdminInterManage;class SystemManage extends AuthAdminInterManage{    public function save_setting(){        $all = Request::instance()->post();        $time = date('Y-m-d H:i:s');        $Setting = Setting::create([            'title'  =>  $all['title'],            'key' =>  $all['key'],            'description' => $all['description'],            'copyright' => $all['copyright'],            'copyright_url' => $all['copyright_url'],            'icp' => $all['icp'],            'icp_url' => $all['icp_url'],            'police' => $all['police'],            'police_url' => $all['police_url'],            'add_time' => $time,        ]);        if($Setting){            $result['status'] = 1;            $result['msg'] = '保存成功！';            return json($result);        }else{            $result['status'] = 0;            $result['msg'] = '保存失败！';            return json($result);        }    }}