<?php/** * Created by PhpStorm. * User: Baymax * Date: 2018/3/17 * Time: 16:00 */namespace app\user\controller;use think\Request;use think\Session;class VisitManage{    public function index(){        $visit = model('Visit');        $user_id = Session::get('user_id');        if(!$user_id){            $user_id = 0;        }        $path = Request::instance()->get();        $visit->insert([            'url' => $path['path'],            'time' => date('Y-m-d H:i:s'),            'user_id' => $user_id,        ]);    }}